import {
  Box,
  Button,
  Card,
  Flex,
  Grid,
  Icon,
  Range,
  Text,
  Tooltip,
} from '@maximeheckel/design-system';
import { motion } from 'framer-motion';
import React, { useMemo, useState } from 'react';
import { HighlightedValue } from '../../Components';

interface CellProps {
  value: number;
  x: number;
  y: number;
  step: number;
}

const Cell = (props: CellProps) => {
  const { value, x, y } = props;

  let color = 'var(--gray-000)';

  if (value > 0.1) {
    color = `rgba(87, 134, 245, ${value})`;
  }

  return (
    <Flex
      alignItems="center"
      css={{
        aspectRatio: '1 / 1',
        borderBottom: '0.5px solid var(--gray-700)',
        borderLeft: '0.5px solid var(--gray-700)',
        backgroundColor: color,
        overflow: 'hidden',
      }}
      justifyContent="center"
    >
      {(x === 0 && y === 0) ||
      (x === 1 && y === 1) ||
      (x === 0 && y === 1) ||
      (x === 1 && y === 0) ||
      (x === 0.5 && y === 0.5) ? (
        <Text role="presentation" css={{ fontSize: 10 }}>
          {x - 0.5},{y - 0.5}
        </Text>
      ) : null}
    </Flex>
  );
};

const fakeSphereSDF = (_x: number, _y: number, step: number) => {
  const alpha = 0.8 * step;

  if (alpha > 5) {
    return 0.0;
  }

  return (
    (1.0 -
      Math.sqrt((_x - 0.5) * (_x - 0.5) + (_y - 0.5) * (_y - 0.5)) * alpha) /
    (alpha * 0.3)
  );
};

const SIZE = 18;
const MAX_STEP = 9;

const VolumetricRaymarchingVisualizer = () => {
  const [step, setStep] = useState(0);

  const emptyMatrix = useMemo(
    () =>
      Array(SIZE)
        .fill(null)
        .map(() => Array(SIZE).fill(null)),
    []
  );

  const matrix = useMemo(
    () =>
      emptyMatrix
        .map((row, y) =>
          row.map((_, x) =>
            fakeSphereSDF(
              x / (row.length - 1),
              y / (emptyMatrix.length - 1),
              MAX_STEP - step + 3.0
            )
          )
        )
        .reverse(),
    [emptyMatrix, step]
  );

  const restart = () => setStep(0);

  const nextStep = () => {
    setStep((prev) => {
      if (prev < MAX_STEP) {
        return prev + 1;
      }
      return prev;
    });
  };

  return (
    <Card css={{ width: '100%' }}>
      <Card.Body
        as={Flex}
        direction="column"
        dotMatrix
        justifyContent="center"
        gap="6"
      >
        <Grid
          css={{
            borderTop: '0.5px solid var(--gray-700)',
            borderRight: '0.5px solid var(--gray-700)',
            overflow: 'hidden',
            width: '100%',
          }}
          templateColumns={`repeat(${SIZE}, 1fr)`}
        >
          {matrix.map((row, idy) => (
            <React.Fragment key={idy}>
              {row.map((value, idx) => (
                <Cell
                  key={`${value}-${idx}-${idy}`}
                  x={idx / (row.length - 1)}
                  y={(matrix.length - 1 - idy) / (matrix.length - 1)}
                  value={value}
                  step={step}
                />
              ))}
            </React.Fragment>
          ))}
        </Grid>

        <Flex
          css={{
            background: 'var(--emphasis)',
            width: '100%',
            height: 'fit-content',
            border: '1px solid var(--border-color)',
            borderRadius: 'var(--border-radius-2)',
            padding: 'var(--space-5)',
          }}
          justifyContent="center"
        >
          <Diagram step={step} />
        </Flex>
        <Flex alignItems="center" css={{ width: '100%' }} gap="3">
          <Tooltip content="Reset">
            <Button icon={<Icon.Repeat />} onClick={restart} variant="icon" />
          </Tooltip>
          <Tooltip content="Next Step">
            <Button
              disabled={step === MAX_STEP}
              icon={<Icon.Arrow />}
              onClick={nextStep}
              variant="icon"
            />
          </Tooltip>
          <Range
            id="timeline"
            aria-label="Timeline"
            min={0}
            max={MAX_STEP}
            step={1}
            value={step}
            onChange={(value) => {
              setStep(value);
            }}
          />
          <Box css={{ flexShrink: 0 }}>
            <Text size="1">
              Step:{' '}
              <HighlightedValue css={{ width: 34 }}>{step}</HighlightedValue>
            </Text>
          </Box>
        </Flex>
      </Card.Body>
    </Card>
  );
};

